Actionscript.script:
stop(); // Stops the Flash timeline from playing the next frame automatically

// --- LEVEL & GAME STATE VARIABLES ---
var levelsString:String = ""; // Will hold the raw text loaded from levels.txt
var levelCount:Number = 53; // Total number of levels in the game
var f:Number = 19; // A constant used later for offset calculations in character spawning
var levels:Array = new Array(levelCount); // 3D Array storing the grid/tiles for every level
var startLocations:Array = new Array(levelCount); // Coordinates for where entities spawn
var locations:Array = new Array(6); // General location tracking array
var bgs:Array = new Array(levelCount); // Background IDs for each level
var levelStart:Number = 0; // The current reading position/index within levelsString
var levelWidth:Number = 0; // Width of the currently parsing level
var levelHeight:Number = 0; // Height of the currently parsing level
var thisLevel:Array = new Array(0); // Temporary array for the current level's data
var switchable:Array = new Array(6); // Likely tracks switch/button states in the game
var charCount:Number = 0; // Number of characters/entities in the current level
var charCount2:Number = 0; // Secondary character counter
var playMode:Number = 0; // Tracks the current game state/mode
var lineCount:Number = 0; // Number of dialogue lines in the current level
var lineLength:Number = 0; // Length of the current string being parsed

// --- DIALOGUE & LEVEL METADATA ---
var dialogueChar:Array = new Array(levelCount); // Who is speaking
var dialogueText:Array = new Array(levelCount); // What they are saying
var dialogueFace:Array = new Array(levelCount); // Character facial expression ID
var levelName:Array = new Array(levelCount); // The display name of the level
var mdao:Array = new Array(levelCount); // Unknown metadata array (possibly timing or score limits)
var mdao2:Number = 0; // Accumulator for the mdao data
var levelProgress:Number; // How many levels the player has beaten

// --- COLLECTIBLES & SAVE DATA ---
var gotCoin:Array; // Array of booleans tracking which levels you found the coin in
var gotThisCoin:Boolean = false; // Tracks if the coin in the current level was collected
var tileCount:Number = 11; // Total number of unique tile types available
var bfdia5b:SharedObject = SharedObject.getLocal("bfdia5b"); // AS2's version of a browser cookie for saving game data locally
var deathCount:Number; // Total player deaths
var timer:Number; // Speedrun timer or total playtime
var coins:Number; // Total coins collected
var longMode:Boolean = false; // Flag for levels that require reading 2 characters per tile

// --- INITIALIZATION ---
// Check if save data exists. If not, set everything to zero. If yes, load it.
if(bfdia5b.data.levelProgress == undefined){
	clearVars();
} else {
	levelProgress = bfdia5b.data.levelProgress;
	gotCoin = new Array(levelCount);
	coins = 0;
	for(var i:Number = 0; i < levelCount; i++){
		gotCoin[i] = bfdia5b.data.gotCoin[i];
		if(gotCoin[i]) coins++; // Count total coins based on the boolean array
	}
	deathCount = bfdia5b.data.deathCount;
	timer = bfdia5b.data.timer;
}

// Resets all game progress back to zero
function clearVars(){
	deathCount = timer = coins = levelProgress = 0;
	gotCoin = new Array(levelCount);
	for(var i:Number = 0; i < levelCount; i++){
		gotCoin[i] = false;
	}
}

// Saves current variables into the SharedObject and writes it to the hard drive
function saveGame(){
	var savedCoins:Array = new Array(levelCount);
	for(var i:Number = 0; i < levelCount; i++){
		savedCoins[i] = gotCoin[i];
	}
	bfdia5b.data.gotCoin = savedCoins;
	bfdia5b.data.coins = coins;
	bfdia5b.data.levelProgress = levelProgress;
	bfdia5b.data.deathCount = deathCount;
	bfdia5b.data.timer = timer;
	bfdia5b.flush(); // Forces the save to disk immediately
}

// --- STRING PARSING HELPERS ---
// Returns a numeric value based on character code, offset by 48 (ASCII for '0')
function charAt(j:Number){
	return levelsString.charCodeAt(j+levelStart)-48;
}

// Returns the actual string character at the given offset
function charAt2(j:Number){
	return levelsString.charAt(j+levelStart);
}

// Decodes specific ASCII characters into tile IDs for the game grid
function tileAt(j:Number, i:Number, y:Number){
	var num:Number = levelsString.charCodeAt(j+levelStart);
	if(num == 128) return 93;
	if(num <= 126){
		return num-46;
	} else if(num <= 182){
		return num-80;
	} else {
		return num-81;
	}
}

// --- LEVEL LOADER & PARSER ---
var lv = new LoadVars(); // AS2 object to load external text files
lv.onLoad = function(success){
	if(success){
		_root.levelsString = this.loadedLevels; // Grabs the text from the loaded file

		// Loop through every single level and parse its data out of the giant text string
		for(var i:Number = 0; i < levelCount; i++){
			levelStart += 2; // Skip delimiter
			lineLength = 0;
			levelName[i] = "";

			// Parse Level Name (stops when it hits ASCII -35, usually a specific delimiter like '#')
			while(charAt(lineLength) != -35){
				levelName[i] += charAt2(lineLength);
				lineLength++;
			}
			levelStart += lineLength;

			// Parse Level Dimensions, Character Count, and Background ID
			levelWidth = 10*charAt(2)+charAt(3);
			levelHeight = 10*charAt(5)+charAt(6);
			charCount = 10*charAt(8)+charAt(9);
			bgs[i] = 10*charAt(11)+charAt(12);

			// Check if this level uses "long mode" (more complex tile data)
			longMode = false;
			if(charAt(14) == 24) longMode = true;

			// Initialize the 2D grid for this specific level
			levels[i] = new Array(levelHeight);
			for(var j:Number = 0; j < levelHeight; j++){
				levels[i][j] = new Array(levelWidth);
			}

			// Fill the level grid with tile IDs
			if(longMode){
				// Reads 2 characters per tile to allow for hundreds of tile types
				for(var y:Number = 0; y < levelHeight; y++){
					for(var x:Number = 0; x < levelWidth; x++){
						levels[i][y][x] = 111*tileAt(y*(levelWidth*2+2)+x*2+17,i,y)+tileAt(y*(levelWidth*2+2)+x*2+18,i,y);
					}
				}
				levelStart += levelHeight*(levelWidth*2+2)+17;
			} else {
				// Reads 1 character per tile
				for(var y:Number = 0; y < levelHeight; y++){
					for(var x:Number = 0; x < levelWidth; x++){
						levels[i][y][x] = tileAt(y*(levelWidth+2)+x+17,i,y);
					}
				}
				levelStart += levelHeight*(levelWidth+2)+17;
			}

			// Parse Start Locations for Characters/Entities
			startLocations[i] = new Array(charCount);
			for(var j:Number = 0; j < charCount; j++){
				startLocations[i][j] = new Array(6);
				// Read coordinates and IDs
				for(var k:Number = 0; k < (f-1)/3; k++){
					startLocations[i][j][k] = charAt(k*3)*10+charAt(k*3+1);
				}
				levelStart += f-2;

				// Handle entities that require extra parameters (like signs or special triggers)
				if(startLocations[i][j][5] == 3 || startLocations[i][j][5] == 4){
					levelStart++;
					startLocations[i][j].push(new Array(0));
					lineLength = 0;
					// Read extra parameter string
					while(charAt(lineLength) != -35){
						startLocations[i][j][6].push(charAt(lineLength));
						lineLength++;
					}
					levelStart += lineLength;
				}
				levelStart += 2;
			}

			// Parse Dialogue
			lineCount = 10*charAt(0)+charAt(1); // How many lines of dialogue in this level?
			levelStart += 4;
			dialogueText[i] = new Array(lineCount);
			dialogueChar[i] = new Array(lineCount);
			dialogueFace[i] = new Array(lineCount);

			// Read each line of dialogue
			for(var j:Number = 0; j < lineCount; j++){
				dialogueChar[i][j] = 10*charAt(0)+charAt(1); // Who is talking

				// Determine facial expression
				if(charAt(2) == 24){
					dialogueFace[i][j] = 2;
				} else {
					dialogueFace[i][j] = 3;
				}
				levelStart += 4;

				// Read the actual spoken text
				lineLength = 0;
				dialogueText[i][j] = "";
				while(charAt(lineLength) != -35){
					lineLength++;
					dialogueText[i][j] += charAt2(lineLength-1);
				}
				levelStart += lineLength+2;
			}

			// Parse final metadata (possibly a target time or score threshold)
			mdao2 += 100000*charAt(0)+10000*charAt(1)+1000*charAt(2)+100*charAt(3)+10*charAt(4)+charAt(5);
			mdao[i] = mdao2;
			levelStart += 8;
		}

		// Once everything is loaded and parsed, move the Flash playhead to the actual game
		gotoAndStop("game");
	}
}
// Trigger the file load
lv.load("levels.txt");

