e,false,false,true,0,0,false,false], // 131
[false,false,false,false,false,false,false,false,false,false,false,0,1,false,true], // 132: Liquid Block (Switchable Group 1) [true,true,true,true,false,false,false,false,false,false,false,0,0,true,false], // 133: Standard Solid Block (with borders) [false,false,false,false,false,false,false,false,false,false,true,0,0,false,false] // 134: Background Tile (Shadable) ]; // Array mapping out the IDs of blocks that change into other blocks when a switch/lever is hit. var switches:Array = [[31,33,32,34,79,78,81,82],[51,53,52,54],[65,61,60,62,63,64],[],[],[14,16,83,85]];
// --- CHARACTER STATS --- // Contains physics and state values for each playable character/entity. // Tracks: [width/2, height, weight, carry-height, friction, can be cached, heat speed] var charD:Array = [ [28,45.4,0.45,27,0.8,false,1], // [0] Ruby's stats [23,56,0.36,31,0.8,false,1.7], // [1] Book's stats [20,51,0.41,20,0.85,false,5], // [2] Ice Cube's stats [10,86,0.26,31,0.8,false,1.6], // [3] Match's stats [10,84,0.23,31,0.8,false,1.4], // [4] Pencil's stats [28,70,0.075,28,0.8,false,9] // [5] Bubble's stats // ... (More entities/objects follow below) ];
// Names of the main playable characters var names:Array = ["Ruby","Book","Ice Cube","Match","Pencil","Bubble"];
// --- LEVEL INITIALIZATION --- // This function prepares a level to be played when it loads function resetLevel(){ charCount = startLocations[currentLevel].length; // Get number of characters spawning in this level levelWidth = levels[currentLevel][0].length; // Get grid width levelHeight = levels[currentLevel].length; // Get grid height
copyLevel(levels[currentLevel]); // Create a working copy of the level grid to manipulate
charDepth = levelWidth*levelHeight+charCount*2; // Calculate the Z-depth (layer order) to render characters above the background charCount2 = 0; // Secondary counter for main playable characters HPRC1 = HPRC2 = 1000000; // Trackers for specific special entities (like the HPRC)
// Loop through every character/entity meant to spawn in the level for(var i:Number = 0; i < charCount; i++){ var id:Number = startLocations[currentLevel][i][0]; // Get the ID of the entity to spawn
// Create a new Character instance with its specific stats from `charD`, its location, and behavior properties char[i] = new Character(id, startLocations[currentLevel][i][1]*30+startLocations[currentLevel][i][2]*30/100, startLocations[currentLevel][i][3]*30+startLocations[currentLevel][i][4]*30/100, 70+i*40, 400-i*30, 0, 0, false, 4, false, 0, 200, 200, 30, startLocations[currentLevel][i][5], -1, new Array(0), charD[id][0], charD[id][1], charD[id][2], charD[id][2], charD[id][3], false, charD[id][4], 0, 2, 0, new Array(0), 0, 0, 0, 0, charD[id][6]);
if(id <= 5) charCount2++; // If ID is 0-5, it's one of the main 6 characters if(id == 36) HPRC1 = i; // Specifically tag ID 36 if(id == 35) HPRC2 = i; // Specifically tag ID 35
// If the character is in a specific pre-defined state (3 or 4), apply a custom speed if(char[i].charState == 3 || char[i].charState == 4) char[i].speed = startLocations[currentLevel][i][6][0]*10+startLocations[currentLevel][i][6][1]; }
drawLevel(); // Call function to actually build the visual tiles drawCharacters(); // Call function to visually place the characters
// Reset various game states recover = false; cornerHangTimer = 0; charsAtEnd = 0; control = 0; cutScene = 0; white._visible = true; // Screen transition overlay
// Calculate background scaling based on level size to ensure it covers the screen bgXScale = ((levelWidth-32)*10+960)/9.6; bgYScale = ((levelHeight-18)*10+540)/5.4; bg._xscale = Math.max(bgXScale, bgYScale); bg._yscale = Math.max(bgXScale, bgYScale); bg.gotoAndStop(bgs[currentLevel]+1); // Set background image based on level data
// Cache static elements as bitmaps to improve performance (Standard Flash game optimization) levelShadow.cacheAsBitmap = true; levelStill.cacheAsBitmap = true; bg.cacheAsBitmap = true;
// Set initial camera position based on the first character's position, clamping to level bounds cameraX = Math.min(Math.max(char[0].x-480,0),levelWidth*30-960); cameraY = Math.min(Math.max(char[0].y-270,0),levelHeight*30-540);
// Update UI text for level number and name levelButtons.textie.text = numberToText(currentLevel+1,true)+". "+levelName[currentLevel]; gotThisCoin = false; // Reset level collectible flag levelTimer = 0; // Reset in-game timer levelTimer2 = getTimer(); // Set actual system time reference for accurate time tracking
// If the main character is in a playable state, give the player control if(char[0].charState <= 9) changeControl(); }
// --- UTILITY FUNCTIONS --- // Creates a deep copy of a level's 2D array grid so the original data isn't overwritten during gameplay function copyLevel(thatLevel:Array){ thisLevel = new Array(thatLevel.length); for(var y:Number = 0; y < levelHeight; y++){ var sourceRow:Array = thatLevel[y]; var copiedRow:Array = new Array(sourceRow.length); for(var x:Number = 0; x < levelWidth; x++){ copiedRow[x] = sourceRow[x]; // Duplicate every tile row by row } thisLevel[y] = copiedRow; } }
// Formats a number into a string with leading zeros (e.g., 5 becomes "05" or "005") function numberToText(i:Number, hundreds:Boolean){ if(hundreds){ if(i < 10) return "00"+i; else if(i < 100) return "0"+i; else return i; } else { if(i == 0) return "00"; else if(i < 10) return "0"+i; else return i; } }
// Converts raw milliseconds into a formatted Hours:Minutes:Seconds.Deciseconds string for speedruns function toHMS(i:Number){ var h:Number = Math.floor(i/3600000); var m:Number = Math.floor(i/60000)%60; var s:Number = Math.floor(i/1000)%60; var ds:Number = Math.floor(i/100)%10; return numberToText(h,false)+":"+numberToText(m,false)+":"+numberToText(s,false)+"."+ds; }
// --- RENDERING FUNCTIONS --- // Interprets the level grid array and attaches MovieClips for blocks and tiles function drawLevel(){ if(playMode == 0 && currentLevel >= 1){ removeTileMovieClips(); // Clear previous level's tiles to prevent lag addTileMovieClips(); }
// Initialize the arrays for interactable switches for(var i:Number = 0; i < 6; i++){ switchable[i] = new Array(0); }
var currentBG:Number = bgs[currentLevel]; for(var y:Number = 0; y < levelHeight; y++){ var row:Array = thisLevel[y]; for(var x:Number = 0; x < levelWidth; x++){ var tileType:Number = row[x]; if(tileType >= 1){ // If a tile exists here var properties:Array = blockProperties[tileType]; var switchID:Number = properties[12]; if(switchID >= 1){ switchable[switchID-1].push([x,y]); }
if(properties[14]){ // Liquid tile addTileMovieClip(x,y,levelActive3); } else if(properties[11] >= 1){ // Lever/Button tile addTileMovieClip(x,y,levelActive2); if(properties[11] >= 7 && properties[11] <= 12){ levelActive2["tileX"+x+"Y"+y].lever._rotation = 60; } } else if(properties[8]){ // Active moving block tile addTileMovieClip(x,y,levelActive); } else { // Standard static block tile addTileMovieClip(x,y,levelStill); }
if(tileType == 6){ locations[0] = x; locations[1] = y; if(currentBG == 9 || currentBG == 10) levelActive["tileX"+x+"Y"+y].bg.gotoAndStop(2); } else if(tileType == 12){ locations[2] = x; locations[3] = y; locations[4] = 1000; locations[5] = 0; } } } } }
// Attaches the actual graphical tile movie clip from the Flash library to the stage function addTileMovieClip(x:Number, y:Number, level:MovieClip){ var t:Number = thisLevel[y][x]; var tileKey:String = "tileX"+x+"Y"+y; var tileDepth:Number = y*levelWidth+x; level.attachMovie("tile"+Math.floor(t/10),tileKey,tileDepth,{_x:x*30,_y:y*30}); var tileClip:MovieClip = level[tileKey]; tileClip.gotoAndStop(t%10+1); // Frame handles graphical variations
var properties:Array = blockProperties[t]; if(t == 6){ // Special glowing block needs wide lighting tileClip.light.gotoAndStop(charCount2); for(var x2:Number = 0; x2 < 2; x2++){ for(var y2:Number = 0; y2 < 4; y2++){ setAmbientShadow(x-x2,y-y2); } } } else if(t >= 110 && t <= 129){ // Large blocks need broader shadows for(var x2:Number = 0; x2 < 3; x2++){ for(var y2:Number = 0; y2 < 2; y2++){ setAmbientShadow(x-x2,y-y2); } } } else if(properties[10]){ // Standard block property that requests shadow setAmbientShadow(x,y); }
if(properties[13]){ setBorder(x,y,levelStill[tileKey].tileBorder,t); } }
// Calculates and attaches shadows around tiles depending on whether neighboring tiles are solid function setAmbientShadow(x:Number, y:Number){ var tileKey:String = "tileX"+x+"Y"+y; levelShadow.attachMovie("tileShadow",tileKey,y*levelWidth+x,{_x:x*30,_y:y*30}); var count:Number = 0; var shadowBits:Array = [8,4,2,1]; for(var i:Number = 0; i < 4; i++){ var nx:Number = x+cardinal[i][0]; var ny:Number = y+cardinal[i][1]; var neighborType:Number = thisLevel[ny][nx]; var neighborProperties:Array = blockProperties[neighborType]; var switchType:Number = neighborProperties[12]; if(neighborProperties[i] && (switchType == 0 || switchType == 6)){ count += shadowBits[i]; } } levelShadow[tileKey].ambientShadow.gotoAndStop(count+1); // Move to correct shadow visual state
// (Secondary shadow logic omitted for brevity, handles corners/diagonals) }
// Helper to prevent Array Out of Bounds errors when checking tiles outside the level boundaries function outOfRange(x:Number, y:Number){ return (x<0 || y<0 || x>=levelWidth || y>=levelHeight); }
// Places character movie clips on the stage function drawCharacters(){ var lastCount:Number; if(playMode == 0 && currentLevel >= 1){ lastCount = startLocations[currentLevel-transitionType].length; } else { lastCount = startLocations[currentLevel].length; } for(var i:Number = 0; i < lastCount; i++){ levelChar["char"+i].removeMovieClip(); } for(var i:Number = 0; i < charCount; i++){ var charData:Object = char[i]; var charKey:String = "char"+i; levelChar.attachMovie("char",charKey,charDepth-i*2,{_x:charData.x,_y:charData.y}); var charClip:MovieClip = levelChar[charKey]; charClip.gotoAndStop(charData.id+1); // Go to character's graphical frame charClip.leg1.gotoAndStop(1); // Reset walk cycle leg 1 charClip.leg2.gotoAndStop(1); // Reset walk cycle leg 2
if(charData.charState <= 1) charClip._visible = false; // Hide if inactive if(charData.charState == 9){ // Dead/trapped state charData.dire = 2; // Force them to look forward charClip.charBody.gotoAndStop(2); charClip.charBody.mouth.gotoAndStop(3); charClip.charBody.mouth.mouth.gotoAndStop(57); // Frown/Trapped face } } }
// --- CUTSCENES & DIALOGUE --- function startCutScene(){ if(cutScene == 0){ if(toSeeCS){ // If cutscene hasn't been skipped/played yet cutScene = 1; cutSceneLine = 0; // Start at the first dialogue line displayLine(currentLevel, cutSceneLine); char[control].dire = Math.ceil(char[control].dire/2)*2; // Make player face forward to talk } else { rescue(); // Skip cutscene and process events immediately for(var i:Number = 0; i < dialogueChar[currentLevel].length; i++){ var p:Number = dialogueChar[currentLevel][i]; if(p >= 50 && p < 60) leverSwitch(p-50); // Instantly toggle levers that would have triggered during the cutscene } cutScene = 3; } } }
// Puts dialogue into the speech bubble clip and animates faces function displayLine(level:Number, line:Number){ var p:Number = dialogueChar[level][line]; // Who is speaking based on ID
// If ID is a switch trigger, toggle it instead of speaking if(p >= 50 && p < 60){ leverSwitch(p-50); cutSceneLine++; line++; p = dialogueChar[level][line]; }
// Position speech bubble based on character location var x:Number; if(p == 99){ // 99 means system text / invisible narrator x = 480; // Center screen horizontally } else { // Clamp bubble to screen edges so it doesn't get cut off x = Math.min(Math.max(char[p].x,bubWidth/2+bubMargin),960-bubWidth/2-bubMargin); putDown(p); }
_root.csBubble.gotoAndPlay(2); // Play bubble pop-in animation _root.csBubble._x = x;
// Decide whether bubble goes above or below character based on camera height if(char[control].y-cameraY > 270) _root.csBubble._y = bubMargin+bubHeight/2; else _root.csBubble._y = 520-bubMargin-bubHeight/2;
// Swap character portrait inside the text bubble if(p == 99){ _root.csBubble.csBubble2.gotoAndStop(2); // Empty/System bubble } else { _root.csBubble.csBubble2.gotoAndStop(1); // Standard character bubble _root.csBubble.csBubble2.box.charBody.gotoAndStop(char[p].id+1); _root.levelChar["char"+p].charBody.mouth.gotoAndStop(dialogueFace[level][line]); // Apply correct facial expression } // Insert text string into the bubble _root.csBubble.csBubble2.textie.text = dialogueText[level][line]; }
// Toggles on/off state blocks based on switch ID mapping function leverSwitch(j:Number){ var switchableIndex:Number = Math.min(j,5); var switchableTiles:Array = switchable[switchableIndex]; var switchPairs:Array = switches[j]; for(var z:Number = 0; z < switchableTiles.length; z++){ var x:Number = switchableTiles[z][0]; var y:Number = switchableTiles[z][1]; var currentTile:Number = thisLevel[y][x]; for(var k:Number = 0; k < switchPairs.length; k += 2){ var fromTile:Number = switchPairs[k]; var toTile:Number = switchPairs[k+1]; if(currentTile == fromTile){ thisLevel[y][x] = toTile; levelActive["tileX"+x+"Y"+y].gotoAndStop(toTile%10+1); break; } else if(currentTile == toTile){ thisLevel[y][x] = fromTile; levelActive["tileX"+x+"Y"+y].gotoAndStop(fromTile%10+1); break; } } } }
// --- COLLISION PHYSICS FUNCTIONS ---
// Checks if a specific tile stops the player from moving through it function solidAt(x:Number, y:Number){ var t:Number = getBlockTypeAt(x,y); // Return true only if it has solid boundaries on all four sides return (blockProperties[t][0] && blockProperties[t][1] && blockProperties[t][2] && blockProperties[t][3]); }
// Returns the tile type ID at the specified grid coordinates (Divided by 30 because each tile is 30x30 pixels) function getBlockTypeAt(x:Number, y:Number){ return thisLevel[Math.floor(y/30)][Math.floor(x/30)]; }
